package com.vengat.java.basics;

public class My02_Variables {
	
	/*
	 * ******* Java Data Types & Variables **********
	 * 
	 * byte (number, 1 byte) [range -128 to +127]
	 * short (number, 2 bytes)
	 * int (number, 4 bytes)
	 * long (number, 8 bytes)
	 * float (float number, 4 bytes)
	 * double (float number, 8 bytes)
	 * char (a character, 2 bytes)
	 * boolean (true or false, 1 byte)
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int my_Variable = 10;
		double my_VariableDouble = 12.23;
		char my_CharecterVariable = 'V';
		boolean my_VariableBoolean = true;
		
		System.out.println("*********** Its sample program for variables & datatypes in Java ***********");
		System.out.println("Integer value :" +my_Variable);
		System.out.println("Double value :" +my_VariableDouble);
		System.out.println("Double value :" +my_CharecterVariable);
		System.out.println("Boolean value :" +my_VariableBoolean);
		

	}

}
