package com.vengat.java.basics;

public class My06_ComparisonOperators {
	
	/*
	 * 	Comparison Operators in Java
	 * 	== 	is equal to 
	 * 	!= 	is not equal to
	 * 	>	is greater than
	 * 	<	is less than
	 * 	>=	is greater than or equal to
	 * 	<= 	is less than or equal to
	 * 
	 */

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b = 20;
		
		System.out.println("10 & 20 is Equal :" + (a==b));
		System.out.println("10 & 20 is Not Equal :" + (a!=b));
		System.out.println("10 & 20 is Greather Than :" + (a>b));
		System.out.println("10 & 20 is Less Than :" + (a<b));
		System.out.println("10 & 20 is Greater Than or Equal :" + (a>=b));
		System.out.println("10 & 20 is Less Than or Equal :" + (a<=b));

	}

}
