package com.vengat.java.basics;

public class My11_Looping_DoWhile {
	/*
	 * do{
	 * state1
	 * state2
	 * }while(condition)
	 */

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int value = 0;
		
		do {
			System.out.println(value);
			value++;
		}while(value<=10);

	}

}
