package com.vengat.java.basics;

public class My12_LoopingFor {
	/*
	 * For Loop in Java
	 * 	for(index;condition;increment/decrement){
	 * 	statements;
	 * 	}
	 */

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int[] myValues = { 1, 2, 3, 4, 5, 6, 7, 8, 9 };
//		For loop
		
		
		
//		For each loop
		for (int element : myValues) {
			System.out.println(myValues[element]);			
		}
		

	}

}
