package com.vengat.java.basics;

public class My14_StringProgram {
	/*
	 * Java String -> Java String is sequence of characters and statements
	 */

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String myString = "Hello Vengatesan" + " Rathinavel";
		
		System.out.println(myString.length());
		System.out.println(myString.indexOf('n'));
		System.out.println(myString.toLowerCase());
		System.out.println(myString.toUpperCase());
		System.out.println(myString.replace('o', '0'));
		

	}

}
